import Header from "./components/Header";
import Tasks from "./components/Tasks";
import Footer from "./components/Footer";
import { useState } from "react";
import AddForm from "./components/AddForm";
function App() {
  this.state = {
    msg: "",
  }
  this.handleCallback = (childData) =>{
    this.setState({msg: childData})
}
  const {msg} = this.state;
  let addBtn = 'passive'
  let bool = true
  const [tasks , setTasks] = useState([
    {
      id: 1,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 2,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 3,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
  ])

  const toggleAdd = (classToggle) =>{
    if (classToggle) {
        classToggle = false
        addBtn = ''
    } else  {
        addBtn = 'passive'
        classToggle = true
    }
    console.log(classToggle)

}



  const deleteTask = (id) =>{
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    <div className="App">
      <h1>{}</h1>
      <Header btnClass={addBtn} bool={bool} toggleAdd={toggleAdd} parentCallback = {this.handleCallback}/>
      <AddForm btnClass={addBtn} />
      <Tasks tasks={tasks} onDelete={deleteTask}/>
      <Footer />
    </div>
  );
}

export default App;
