function App() {
  addBtn = 'passive';
  const [tasks , setTasks] = useState([
    {
      id: 1,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 2,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 3,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
  ])

  const deleteTask = (id) =>{
    console.log('deleted' , id)
  }

  return (
    <div className="App">
      <Header />
      <AddForm btnClass='' />
      <Tasks tasks={tasks} onDelete={deleteTask}/>
      <Footer />
    </div>
  );
}

export default App;
