import Button from "./Button"



const Header = ({btnClass}) => {

    const toggleAdd = (classToggle) =>{
        if (btnClass == 'passive') {
            btnClass = 'active'
            console.log(btnClass)
        } else {
            btnClass = 'passive'
            console.log(btnClass)

        }
    }

    return(
        <header>
            <h2>Task Tracker</h2>
            <Button onClick={() => toggleAdd(btnClass) }/>
        </header>
    )
}

export default Header
