import Button from "./Button"
import { FaCheck } from "react-icons/fa"

const Form = () => {
    const checked = () => {
        const check = document.querySelector('.reminder input')
        const newCheck = document.querySelector('.check-icon')
        if (!check.checked) {
            console.log(check.checked)

            check.checked = true;
            newCheck.classList.add('active')
            console.log(check.checked)

        }else{
            console.log(check.checked)

            check.checked = false;
            newCheck.classList.removeClass('active')
            console.log(check.checked)

        }
    }
    return (
        <form>
            <label htmlFor='task'>Task</label>
            <input type='text' placeholder='Add Task' id='task' />
            <label htmlFor='date'>Day & Time</label>
            <input type='datetime-local' placeholder='Add Date & Time' id='date' />
            <div className="reminder">
                <label htmlFor='reminder'>Set Reminder</label>
                <input type='checkbox' id='reminder' />
                <span className="check" onClick={() => checked()}><FaCheck className="check-icon" /></span>
            </div>
            <Button />
        </form>
    )
}

export default Form
