import Button from "./Button"
import { FaCheck } from "react-icons/fa"

const Form = () => {
    const checked = () => {
        const check = document.querySelector('.reminder input')
        const newCheck = document.querySelector('.check-icon')
        if (!check.checked) {
            check.checked = true;
            newCheck
        }
    }
    return (
        <form>
            <label for='task'>Task</label>
            <input type='text' placeholder='Add Task' id='task' />
            <label for='date'>Day & Time</label>
            <input type='datetime-local' placeholder='Add Date & Time' id='date' />
            <div className="reminder">
                <label for='reminder'>Set Reminder</label>
                <input type='checkbox' id='reminder' />
                <span className="check"><FaCheck className="check-icon" onClick={() => checked()}/></span>
            </div>
            <Button />
        </form>
    )
}

export default Form
