import Header from "./components/Header";
import Tasks from "./components/Tasks";
import Footer from "./components/Footer";
import { useState } from "react";
import AddForm from "./components/AddForm";
function App() {


  const [currentClasses,setClass] = useState([
    {
      class1 : "passive",
      class2 : "active"  
    }
  ]);
  const [tasks , setTasks] = useState([
    {
      id: 1,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 2,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 3,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
  ])

  const toggleAdd = (classToggles) =>{
    if (classToggles.class1==='passive') {
        setClass(state => {
          return {}
        })
    } else  {
        setClass('passive')
    }
    console.log(classToggles)

}



  const deleteTask = (id) =>{
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    <div className="App">
      <Header btnClass={currentClasses}  toggleAdd={toggleAdd} />
      <AddForm btnClass={currentClasses} />
      <Tasks tasks={tasks} onDelete={deleteTask}/>
      <Footer />
    </div>
  );
}

export default App;
