      id: 1,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 2,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 3,
      text: 'Doctors appointment',
      date: 'Feb 5th at 2:30pm',
      reminder: true
    },
  ])

  const toggleAdd = (classToggles) =>{
    if (classToggles==='passive') {
        setClass("")
    } else  {
      setClass("passive")
    }
    console.log(classToggles)

  }

  const toggleReminder = (id) =>{
    setTasks(tasks.map((task) => {
      if (task.id === id) {
        setTasks(state => {
          return {...state,reminder:true}
        })
