      }
    }))
  }


  const deleteTask = (id) =>{
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    <div className="App">
      <Header btnClass={currentClasse}  toggleAdd={toggleAdd} />
      <AddForm btnClass={currentClasse} />
      <Tasks toggleReminder={toggleReminder} tasks={tasks} onDelete={deleteTask}/>
      <Footer />
    </div>
  );
}

export default App;
